319      public static void migratePropertySourceKeys(Map<String, String> keyMaps, PropertySource<?> src, Map<String, Object> migrated) {
320          Map<String, Object> temp = new HashMap<>();
321          // needs to be processed serially
322          keyMaps.entrySet().stream()
323                  // we're not migrating to null, i.e. trying to delete the property
324                  .filter(e -> e.getValue() != null)
325                  // ps has the property we're trying to migrate (either directly or migrated earlier within the chain)
326                  .filter(e -> StringUtils.isNotBlank(Optional.ofNullable(src.getProperty(e.getKey())).map(Object::toString).orElse(temp.getOrDefault(e.getKey(), "").toString())))
327                  // we're not migrating to a property that is already occupied
328                  .filter(e -> StringUtils.isBlank(Optional.ofNullable(src.getProperty(e.getValue())).map(Object::toString).orElse(temp.getOrDefault(e.getValue(), "").toString())))
329                  .forEach(e -> {
330                      Object val = Optional.ofNullable(src.getProperty(e.getKey())).orElse(temp.get(e.getKey()));
331                      temp.put(e.getValue(), val);
332                      if (migrated.containsKey(e.getValue())) {
333                          LOG.info("Skipping migrating property [{}] to [{}] in {} (already migrated)", e.getKey(), e.getValue(), src.getName());
334                      } else {
335                          LOG.info("Migrating property [{}] to [{}] in {}", e.getKey(), e.getValue(), src.getName());
336                          migrated.put(e.getValue(), val);
337                      }
338                  });
339      }
