341      public static void migratePropFileKeys(Map<String, String> keyMaps, ConfigurationPropertiesService cps) {
342          Boolean retainObsoleteKeys = Optional.ofNullable(cps.getProperty(KEY_PROPERTIES_FILE_RETAIN_OBSOLETE_KEYS)).map(x -> Boolean.valueOf((String) x)).orElse(true);
343  
344          // needs to be processed serially
345          keyMaps.entrySet().stream().filter(e -> cps.containsKey(e.getKey())).forEach(e -> {
346              if (e.getValue() != null && !cps.containsKey(e.getValue())) {
347                  LOG.info("Migrating property [{}] to [{}] in properties file", e.getKey(), e.getValue());
348                  cps.setProperty(e.getValue(), cps.getProperty(e.getKey()));
349              }
350  
351              // delete old property
352              if (!retainObsoleteKeys) {
353                  LOG.info("Removing obsolete property [{}] in properties file", e.getKey());
354                  cps.clearProperty(e.getKey());
355              }
356          });
357  
358          cps.save();
359      }
