129      /**
130       * Return an URL for the given HTTP request, with anonymized sensitive parameters.
131       *
132       * @param request An HTTP request instance
133       * @return The associated anonymized URL
134       */
135      public static String getAnonymizedURLForRequest(HttpServletRequest request) {
136  
137          String url = getURLForRequest(request);
138          UriComponentsBuilder builder = UriComponentsBuilder.fromUriString(url);
139          MultiValueMap<String, String> components = builder.build().getQueryParams();
140  
141          // Subsonic REST API authentication (see RESTRequestParameterProcessingFilter)
142          if (components.containsKey("p")) builder.replaceQueryParam("p", URL_SENSITIVE_REPLACEMENT_STRING);  // Cleartext password
143          if (components.containsKey("t")) builder.replaceQueryParam("t", URL_SENSITIVE_REPLACEMENT_STRING);  // Token
144          if (components.containsKey("s")) builder.replaceQueryParam("s", URL_SENSITIVE_REPLACEMENT_STRING);  // Salt
145          if (components.containsKey("u")) builder.replaceQueryParam("u", URL_SENSITIVE_REPLACEMENT_STRING);  // Username
146  
147          return builder.build().toUriString();
148      }
