426      private static boolean isExecutableInstalled(String executable) {
427          Process process = null;
428          try {
429              process = new ProcessBuilder(executable).start();
430              LOG.debug("{} exists and can be executed. Last executed {}", executable, process.info().startInstant());
431              return true;
432          } catch (IOException e) {
433              LOG.debug("{} cannot be executed", executable, e);
434              return false;
435          } finally {
436              if (process != null) {
437                  process.destroy();
438              }
439          }
440      }
