 77      @GetMapping
 78      public ResponseEntity<Resource> handleRequest(
 79              Authentication authentication,
 80              @RequestParam int id,
 81              @RequestParam(required = false) String captionId,
 82              @RequestParam(required = false, name = "format") String requiredFormat,
 83              HttpServletRequest request)
 84              throws Exception {
 85  
 86          User user = securityService.getUserByName(authentication.getName());
 87          MediaFile video = this.mediaFileService.getMediaFile(id);
 88          if (!(authentication instanceof JWTAuthenticationToken)
 89                  && !securityService.isFolderAccessAllowed(video, user.getUsername())) {
 90              throw new AccessDeniedException("Access to file " + id + " is forbidden for user " + user.getUsername());
 91          }
 92  
 93          List<CaptionInfo> captions = listCaptions(video, NetworkService.getBaseUrl(request));
 94          CaptionInfo res;
 95          if (captionId == null) {
 96              res = captions.stream().findFirst().orElse(null);
 97          } else {
 98              res = captions.stream().filter(c -> StringUtils.equalsIgnoreCase(captionId, c.getIdentifier())).findAny().orElse(null);
 99          }
100  
101          if (res == null) {
102              throw new NotFoundException("No captions found for file id: " + id);
103          }
104  
105          String effectiveFormat = requiredFormat != null ? requiredFormat : res.getFormat();
106  
107          Resource resource = null;
108          Instant time = null;
109  
110          if (res.getLocation() == CaptionInfo.Location.external) {
111              Path captionsFile = Paths.get(res.getIdentifier());
112  
113              if (effectiveFormat.equalsIgnoreCase(res.getFormat())) {
114                  resource = getExternalResource(captionsFile, res.getFormat());
115              } else if ("srt".equals(res.getFormat()) && "vtt".equals(requiredFormat)) {
116                  resource = getConvertedResource(captionsFile, "0", effectiveFormat);
117              } else {
118                  throw new NotFoundException("No captions found for file id: " + id);
119              }
120              time = Files.getLastModifiedTime(captionsFile).toInstant();
121          } else {
122              Path videoFullPath = video.getFullPath(mediaFolderService.getMusicFolderById(video.getFolderId()).getPath());
123              resource = getConvertedResource(videoFullPath, res.getIdentifier(), effectiveFormat);
124              time = Files.getLastModifiedTime(videoFullPath).toInstant();
125          }
126  
127          HttpHeaders headers = new HttpHeaders();
128          headers.setContentType(CAPTION_FORMAT_VTT.equalsIgnoreCase(effectiveFormat)
129                  ? new MediaType("text", "vtt", StandardCharsets.UTF_8)
130                  : new MediaType("text", "plain", StandardCharsets.UTF_8));
131          headers.setAccessControlAllowOrigin("*");
132  
133  
134          return ResponseEntity.ok()
135                  .lastModified(time)
136                  .headers(headers)
137                  .body(resource);
138      }
