140      public Resource getConvertedResource(Path inputFile, String identifier, String format) throws IOException {
141          String[] split = StringUtils.split(settingsService.getSubtitlesExtractionCommand());
142          List<String> command = Arrays.stream(split).sequential()
143                  .map(i -> {
144                      if (i.equals(split[0])) {
145                          return SettingsService.resolveTranscodeExecutable(i, i);
146                      }
147                      if (i.contains("%s")) {
148                          return i.replace("%s", inputFile.toString());
149                      }
150                      if (i.contains("%i")) {
151                          return i.replace("%i", identifier);
152                      }
153                      if (i.contains("%f")) {
154                          return i.replace("%f", getForceFormat(format));
155                      }
156  
157                      return i;
158                  }).collect(Collectors.toList());
159          Process process = new ProcessBuilder(command).start();
160          Resource resource = new InputStreamResource(process.getInputStream());
161          // Must read stderr from the process, otherwise it may block.
162          new InputStreamReaderThread(process.getErrorStream(), "subs-extraction-error-stream-" + inputFile.toString(), true).start();
163          return resource;
164      }
