209      public List<CaptionInfo> listCaptions(MediaFile video, String basePath, String externalUser, Instant externalExpiration) {
210          MetaData metaData = getVideoMetaData(video);
211          Stream<CaptionInfo> internalCaptions;
212          if (metaData == null || metaData.getSubtitleTracks().isEmpty()) {
213              internalCaptions = Stream.empty();
214          } else {
215              internalCaptions = metaData.getSubtitleTracks().stream()
216                      .map(c -> new CaptionInfo(
217                              String.valueOf(c.getId()),
218                              CaptionInfo.Location.internal,
219                              getDisplayFormat(c.getCodec()),
220                              c.getLanguage(),
221                              getUrl(basePath, externalUser, externalExpiration, video.getId(),
222                                      String.valueOf(c.getId()))));
223          }
224  
225          Stream<CaptionInfo> externalCaptions = findExternalCaptionsForVideo(video).stream()
226                  .map(c -> new CaptionInfo(c.toString(), // leaks internal structure for now
227                          CaptionInfo.Location.external,
228                          MoreFiles.getFileExtension(c),
229                          c.getFileName().toString(),
230                          getUrl(basePath, externalUser, externalExpiration, video.getId(),
231                                  URLEncoder.encode(c.toString(), StandardCharsets.UTF_8))));
232  
233          return Stream.concat(internalCaptions, externalCaptions).collect(Collectors.toList());
234      }
