117      /**
118       * @see InputStream#close()
119       */
120      @Override
121      public void close() {
122          FileUtil.closeQuietly(processInputStream);
123          FileUtil.closeQuietly(processOutputStream);
124  
125          if (process != null) {
126              process.destroy();
127          }
128  
129          if (tmpFile != null) {
130              try {
131                  Files.deleteIfExists(tmpFile);
132              } catch (IOException e) {
133                  LOG.warn("Failed to delete tmp file: {}", tmpFile);
134              }
135          }
136      }
