283      /**
284       * Returns an input stream by applying the given transcoding to the given music file.
285       *
286       * @param parameters Transcoding parameters.
287       * @return The transcoded input stream.
288       * @throws IOException If an I/O error occurs.
289       */
290      private InputStream createTranscodedInputStream(Parameters parameters)
291              throws IOException {
292  
293          Transcoding transcoding = parameters.getTranscoding();
294          Integer maxBitRate = parameters.getMaxBitRate();
295          VideoTranscodingSettings videoTranscodingSettings = parameters.getVideoTranscodingSettings();
296          MediaFile mediaFile = parameters.getMediaFile();
297  
298          TranscodeInputStream in = createTranscodeInputStream(transcoding.getStep1(), maxBitRate, videoTranscodingSettings, mediaFile, null);
299  
300          if (transcoding.getStep2() != null) {
301              in = createTranscodeInputStream(transcoding.getStep2(), maxBitRate, videoTranscodingSettings, mediaFile, in);
302          }
303  
304          if (transcoding.getStep3() != null) {
305              in = createTranscodeInputStream(transcoding.getStep3(), maxBitRate, videoTranscodingSettings, mediaFile, in);
306          }
307  
308          return in;
309      }
