311      /**
312       * Creates a transcoded input stream by interpreting the given command line string.
313       * This includes the following:
314       * <ul>
315       * <li>Splitting the command line string to an array.</li>
316       * <li>Replacing occurrences of "%s" with the path of the given music file.</li>
317       * <li>Replacing occurrences of "%t" with the title of the given music file.</li>
318       * <li>Replacing occurrences of "%l" with the album name of the given music file.</li>
319       * <li>Replacing occurrences of "%a" with the artist name of the given music file.</li>
320       * <li>Replacing occurrences of "%b" with the max bitrate.</li>
321       * <li>Replacing occurrences of "%o" with the video time offset (used for scrubbing).</li>
322       * <li>Replacing occurrences of "%d" with the video duration (used for HLS).</li>
323       * <li>Replacing occurrences of "%w" with the video image width.</li>
324       * <li>Replacing occurrences of "%h" with the video image height.</li>
325       * <li>Prepending the path of the transcoder directory if the transcoder is found there.</li>
326       * </ul>
327       *
328       * @param command                  The command line string.
329       * @param maxBitRate               The maximum bitrate to use. May not be {@code null}.
330       * @param videoTranscodingSettings Parameters used when transcoding video. May be {@code null}.
331       * @param mediaFile                The media file.
332       * @param in                       Data to feed to the process.  May be {@code null}.
333       * @return The newly created input stream.
334       */
335      private TranscodeInputStream createTranscodeInputStream(String command, Integer maxBitRate,
336                                                              VideoTranscodingSettings videoTranscodingSettings, MediaFile mediaFile, InputStream in) throws IOException {
337  
338          // Work-around for filename character encoding problem on Windows.
339          // Create temporary file, and feed this to the transcoder.
340          Path path = mediaFile.getFullPath(mediaFolderService.getMusicFolderById(mediaFile.getFolderId()).getPath()).toAbsolutePath();
341          String pathString = path.toString();
342          Path tmpFile = null;
343          if (Util.isWindows() && !mediaFile.isVideo() && !StringUtils.isAsciiPrintable(path.toString()) && StringUtils.contains(command, "%s")) {
344              tmpFile = Files.createTempFile("airsonic", "." + MoreFiles.getFileExtension(path));
345              tmpFile.toFile().deleteOnExit();
346              Files.copy(path, tmpFile, StandardCopyOption.REPLACE_EXISTING);
347              LOG.info("Created tmp file: {}", tmpFile);
348              pathString = tmpFile.toString();
349          }
350  
351          Map<String, String> vars = generateTranscodingSubstitutionMap(
352                  Optional.ofNullable(mediaFile.getTitle()).orElse("Unknown Media"),
353                  Optional.ofNullable(mediaFile.getArtist()).orElse("Unknown Artist"),
354                  Optional.ofNullable(mediaFile.getAlbumName()).orElse("Unknown Album"),
355                  Optional.ofNullable(maxBitRate).map(String::valueOf).orElse(null),
356                  videoTranscodingSettings != null ? String.valueOf(videoTranscodingSettings.getTimeOffset()) : null,
357                  videoTranscodingSettings != null ? String.valueOf(videoTranscodingSettings.getDuration()) : null,
358                  videoTranscodingSettings != null ? String.valueOf(videoTranscodingSettings.getWidth()) : null,
359                  videoTranscodingSettings != null ? String.valueOf(videoTranscodingSettings.getHeight()) : null,
360                  Optional.ofNullable(maxBitRate).map(TranscodingService::getAverageVideoBitRate).map(String::valueOf).orElse(null),
361                  Optional.ofNullable(maxBitRate).map(TranscodingService::getSuitableAudioBitRate).map(String::valueOf).orElse(null),
362                  Optional.ofNullable(videoTranscodingSettings).map(VideoTranscodingSettings::getAudioTrackIndex).map(String::valueOf).orElse(null),
363                  Optional.ofNullable(videoTranscodingSettings).map(VideoTranscodingSettings::getHlsSegmentIndex).map(String::valueOf).orElse(null),
364                  Optional.ofNullable(videoTranscodingSettings).map(VideoTranscodingSettings::getHlsSegmentFilename).orElse(null),
365                  pathString,
366                  // TODO: this shouldn't be part of videosettings
367                  Optional.ofNullable(videoTranscodingSettings).map(VideoTranscodingSettings::getOutputFilename).orElse(null));
368  
369          ProcessBuilder builder = transformTranscodingVariables(command, vars);
370          return new TranscodeInputStream(builder, in, tmpFile);
371      }
372  
373      public static ProcessBuilder transformTranscodingVariables(String command, Map<String, String> vars) {
374          String[] splitCommand = StringUtil.split(command);
375          List<String> kl = new ArrayList<>(vars.size());
376          List<String> vl = new ArrayList<>(vars.size());
377          vars.entrySet().stream().filter(e -> e.getValue() != null).forEach(e -> {
378              kl.add(e.getKey());
379              vl.add(e.getValue());
380          });
381          String[] ka = kl.toArray(new String[0]);
382          String[] va = vl.toArray(new String[0]);
383          List<String> result = IntStream.range(0, splitCommand.length)
384                  .mapToObj(i -> substituteTranscodingVariable(i, splitCommand[i], ka, va)).collect(Collectors.toList());
385  
386          return new ProcessBuilder(result);
387      }
