389      public static String substituteTranscodingVariable(int index, String commandSegment, String[] keys, String[] values) {
390          if (index == 0) {
391              return SettingsService.resolveTranscodeExecutable(commandSegment, commandSegment);
392          }
393  
394          return StringUtils.replaceEach(commandSegment, keys, values);
395      }
