397      public static Map<String, String> generateTranscodingSubstitutionMap(
398              String title,
399              String artist,
400              String album,
401              String maxVideoBitRate,
402              String timeOffset,
403              String duration,
404              String width,
405              String height,
406              String averageVideoRate,
407              String audioRate,
408              String audioTrackIndex,
409              String hlsSegmentIndex,
410              String hlsSegmentFilename,
411              String inputFilename,
412              String outputFilename) {
413          Map<String, String> result = new HashMap<>();
414          result.put("%t", title);
415          result.put("%a", artist);
416          result.put("%l", album);
417  
418          result.put("%b", maxVideoBitRate);
419          result.put("%o", timeOffset);
420          result.put("%d", duration);
421          result.put("%w", width);
422          result.put("%h", height);
423  
424          result.put("%v", averageVideoRate);
425          result.put("%r", audioRate);
426          result.put("%i", audioTrackIndex);
427          result.put("%j", hlsSegmentIndex);
428          result.put("%n", hlsSegmentFilename);
429          result.put("%s", inputFilename);
430          result.put("%p", outputFilename);
431  
432          return result;
433      }
