435      /**
436       * Returns an applicable transcoding for the given file and player, or <code>null</code> if no
437       * transcoding should be done.
438       */
439      private Transcoding getTranscoding(MediaFile mediaFile, Player player, String preferredTargetFormat, boolean hls) {
440  
441          if (hls) {
442              return new Transcoding(null, "hls", mediaFile.getFormat(), "ts", settingsService.getHlsCommand(), null, null, true);
443          }
444  
445          if (FORMAT_RAW.equals(preferredTargetFormat)) {
446              return null;
447          }
448  
449          List<Transcoding> applicableTranscodings = new LinkedList<Transcoding>();
450          String suffix = mediaFile.getFormat();
451  
452          // This is what I'd like todo, but this will most likely break video transcoding as video transcoding is
453          // never expected to be null
454  //        if(StringUtils.equalsIgnoreCase(preferredTargetFormat, suffix)) {
455  //            LOG.debug("Target formats are the same, returning no transcoding");
456  //            return null;
457  //        }
458  
459          List<Transcoding> transcodingsForPlayer = getTranscodingsForPlayer(player);
460          for (Transcoding transcoding : transcodingsForPlayer) {
461              // special case for now as video must have a transcoding
462              if (mediaFile.isVideo() && StringUtils.equalsIgnoreCase(preferredTargetFormat, transcoding.getTargetFormat())) {
463                  LOG.debug("Detected source to target format match for video");
464                  return transcoding;
465              }
466              for (String sourceFormat : transcoding.getSourceFormatsAsArray()) {
467                  if (sourceFormat.equalsIgnoreCase(suffix)) {
468                      if (isTranscodingInstalled(transcoding)) {
469                          applicableTranscodings.add(transcoding);
470                      }
471                  }
472              }
473          }
474  
475          if (applicableTranscodings.isEmpty()) {
476              return null;
477          }
478  
479          for (Transcoding transcoding : applicableTranscodings) {
480              if (transcoding.getTargetFormat().equalsIgnoreCase(preferredTargetFormat)) {
481                  return transcoding;
482              }
483          }
484  
485          return applicableTranscodings.get(0);
486      }

