 57      private static final Logger LOG = LoggerFactory.getLogger(FFmpegParser.class);
 58      private static final String[] FFPROBE_OPTIONS = {
 59          "-v", "quiet", "-print_format", "json", "-show_format", "-show_streams"
 60      };
 61  
