 67      /**
 68       * Parses meta data for the given music file. No guessing or reformatting is done.
 69       *
 70       *
 71       * @param file The music file to parse.
 72       * @return Meta data for the file.
 73       */
 74      @Override
 75      public MetaData getRawMetaData(Path file) {
 76  
 77          MetaData metaData = new MetaData();
 78  
 79          try {
 80              // Use `ffprobe` in the transcode directory if it exists, otherwise let the system sort it out.
 81              String ffprobe = SettingsService.resolveTranscodeExecutable("ffprobe", "ffprobe");
 82  
 83              List<String> command = new ArrayList<>();
 84              command.add(ffprobe);
 85              command.addAll(Arrays.asList(FFPROBE_OPTIONS));
 86              command.add(file.toAbsolutePath().toString());
 87  
 88              Process process = Runtime.getRuntime().exec(command.toArray(new String[0]));
 89              JsonNode result = null;
 90              try (InputStream in = process.getInputStream(); BufferedInputStream bin = new BufferedInputStream(in);) {
 91                  result = Util.getObjectMapper().readTree(bin);
 92              }
 93  
 94              metaData.setDuration(result.at("/format/duration").asDouble());
 95              // Bitrate is in Kb/s
 96              metaData.setBitRate(result.at("/format/bit_rate").asInt() / 1000);
 97  
 98              metaData.setAlbumArtist(getData(result, "album_artist"));
 99              metaData.setArtist(getData(result, "artist"));
100              metaData.setAlbumName(getData(result, "album"));
101              metaData.setGenre(getData(result, "genre"));
102              metaData.setTitle(getData(result, "title"));
103  
104              String data = getData(result, "track");
105              if (NumberUtils.isCreatable(data)) {
106                  metaData.setTrackNumber(NumberUtils.createInteger(data));
107              }
108  
109              data = getData(result, "date");
110              if (NumberUtils.isCreatable(data)) {
111                  metaData.setYear(NumberUtils.createInteger(data));
112              }
113  
114              // Find the first (if any) stream that has dimensions and use those.
115              // 'width' and 'height' are display dimensions; compare to 'coded_width', 'coded_height'.
116              for (JsonNode stream : result.at("/streams")) {
117                  Track track = new Track(stream.get("index").asInt(), stream.get("codec_type").asText(), stream.at("/tags/language").asText(), stream.get("codec_name").asText());
118                  metaData.addTrack(track);
119  
120                  if (track.isVideo() && stream.has("width") && stream.has("height")) {
121                      metaData.setWidth(stream.get("width").asInt());
122                      metaData.setHeight(stream.get("height").asInt());
123                  }
124              }
125          } catch (Throwable x) {
126              LOG.warn("Error when parsing metadata in {}", file, x);
127          }
128  
129          return metaData;
130      }
131  
132      private static String getData(JsonNode node, String keyName) {
133          List<String> keys = ImmutableList.of("/tags/" + keyName, "/tags/" + keyName.toUpperCase(), "/tags/" + keyName.toLowerCase());
134          Optional<String> nonNullKey = keys.stream().map(k -> node.at("/format" + k).asText()).filter(StringUtils::isNotBlank).findFirst();
135          if (nonNullKey.isPresent()) {
136              return nonNullKey.get();
137          } else {
138              for (JsonNode stream : node.at("/streams")) {
139                  // skip coverart streams
140                  if (stream.hasNonNull("codec_name") && stream.get("codec_name").asText().equalsIgnoreCase("mjpeg")) {
141                      continue;
142                  }
143                  nonNullKey = keys.stream().map(k -> stream.at(k).asText()).filter(StringUtils::isNotBlank).findFirst();
144                  if (nonNullKey.isPresent()) {
145                      return nonNullKey.get();
146                  }
147              }
148          }
149  
150          return null;
151      }
