111              LOG.debug("Got cached sources for internet radio {}!", radio.getStreamUrl());
112              sources = cachedSources.get(radio.getId());
113          } else {
114              LOG.debug("Retrieving sources for internet radio {}...", radio.getStreamUrl());
115              try {
116                  sources = retrieveInternetRadioSources(radio);
117                  if (sources.isEmpty()) {
118                      LOG.warn("No entries found for internet radio {}.", radio.getStreamUrl());
119                  } else {
120                      LOG.info("Retrieved playlist for internet radio {}, got {} sources.", radio.getStreamUrl(), sources.size());
121                  }
122              } catch (Exception e) {
123                  LOG.error("Failed to retrieve sources for internet radio {}.", radio.getStreamUrl(), e);
124                  sources = new ArrayList<>();
125              }
126              cachedSources.put(radio.getId(), sources);
127          }
128          return sources;
