207      /**
208       * Start a new connection to a remote URL, and follow redirects.
209       *
210       * @param url the remote URL
211       * @param maxRedirects maximum number of redirects, or 0 if unlimited
212       * @return an open connection
213       */
214      protected HttpURLConnection connectToURLWithRedirects(URL url, int maxRedirects) throws IOException, PlaylistException {
215  
216          int redirectCount = 0;
217          URL currentURL = url;
218  
219          // Start a first connection.
220          HttpURLConnection connection = connectToURL(currentURL);
221  
222          // While it redirects, follow redirects in new connections.
223          while (connection.getResponseCode() == HttpURLConnection.HTTP_MOVED_PERM ||
224                 connection.getResponseCode() == HttpURLConnection.HTTP_MOVED_TEMP ||
225                 connection.getResponseCode() == HttpURLConnection.HTTP_SEE_OTHER) {
226  
227              // Check if redirect count is not too large.
228              redirectCount += 1;
229              if (maxRedirects > 0 && redirectCount > maxRedirects) {
230                  connection.disconnect();
231                  throw new PlaylistHasTooManyRedirects(String.format("Too many redirects (%d) for URL %s", redirectCount, url));
232              }
233  
234              // Reconnect to the new URL.
235              currentURL = new URL(connection.getHeaderField("Location"));
236              connection.disconnect();
237              connection = connectToURL(currentURL);
238          }
239  
240          // Return the last connection that did not redirect.
241          return connection;
242      }
243  
244      /**
245       * Start a new connection to a remote URL.
246       *
247       * @param url the remote URL
248       * @return an open connection
249       */
250      protected HttpURLConnection connectToURL(URL url) throws IOException {
251          HttpURLConnection urlConnection = (HttpURLConnection) url.openConnection();
252          urlConnection.setAllowUserInteraction(false);
253          urlConnection.setConnectTimeout(10000);
254          urlConnection.setDoInput(true);
255          urlConnection.setDoOutput(false);
256          urlConnection.setReadTimeout(60000);
257          urlConnection.setUseCaches(true);
258          urlConnection.connect();
259          return urlConnection;
260      }
261  }

