157       * @return a list of internet radio sources
158       */
159      private List<InternetRadioSource> retrieveInternetRadioSources(InternetRadio radio, int maxCount, long maxByteSize, int maxRedirects) throws Exception {
160          String streamUrl = radio.getStreamUrl();
161          LOG.debug("Parsing internet radio (playlist) at {}...", streamUrl);
162  
163          SpecificPlaylist inputPlaylist = null;
164          HttpURLConnection urlConnection = connectToURLWithRedirects(new URL(streamUrl), maxRedirects);
165          try (InputStream in = urlConnection.getInputStream();
166                  BoundedInputStream bin = new BoundedInputStream(in, maxByteSize);) {
167              String contentType = urlConnection.getContentType();
168              if (contentType != null && DIRECT_PLAYABLE_TYPES.contains(contentType)) {
169                  //for direct binary streams, just return a collection with a single internet radio source
170                  LOG.debug("Got direct source media at {}", streamUrl);
171                  return Collections.singletonList(new InternetRadioSource(streamUrl));
172              }
173              String contentEncoding = urlConnection.getContentEncoding();
174              inputPlaylist = SpecificPlaylistFactory.getInstance().readFrom(bin, contentEncoding);
175          } finally {
176              urlConnection.disconnect();
177          }
178          if (inputPlaylist == null) {
179              throw new PlaylistFormatUnsupported("Unsupported playlist format " + streamUrl);
180          }
181  
182          // Retrieve stream URLs
183          List<InternetRadioSource> entries = new ArrayList<>();
184          try {
185              inputPlaylist.toPlaylist().acceptDown(new BasePlaylistVisitor() {
186                  @Override
187                  public void beginVisitMedia(Media media) throws Exception {
188                      // Since we're dealing with remote content, we place a hard
189                      // limit on the maximum number of items to load from the playlist,
190                      // in order to avoid parsing erroneous data.
191                      if (maxCount > 0 && entries.size() >= maxCount) {
192                          throw new PlaylistTooLarge("Remote playlist has too many sources (maximum " + maxCount + ")");
193                      }
194                      String streamUrl = media.getSource().getURI().toString();
195                      LOG.debug("Got source media at {}", streamUrl);
196                      entries.add(new InternetRadioSource(streamUrl));
197                  }
198              });
199          } catch (PlaylistTooLarge e) {
200              // Ignore if playlist is too large, but truncate the rest and log a warning.
201              LOG.warn(e.getMessage());
202          }
203  
204          return entries;
205      }
206  
207      /**
208       * Start a new connection to a remote URL, and follow redirects.
209       *
210       * @param url the remote URL
211       * @param maxRedirects maximum number of redirects, or 0 if unlimited
212       * @return an open connection
213       */
214      protected HttpURLConnection connectToURLWithRedirects(URL url, int maxRedirects) throws IOException, PlaylistException {
215  
216          int redirectCount = 0;
217          URL currentURL = url;
218  
219          // Start a first connection.
220          HttpURLConnection connection = connectToURL(currentURL);
221  
222          // While it redirects, follow redirects in new connections.
223          while (connection.getResponseCode() == HttpURLConnection.HTTP_MOVED_PERM ||
224                 connection.getResponseCode() == HttpURLConnection.HTTP_MOVED_TEMP ||
225                 connection.getResponseCode() == HttpURLConnection.HTTP_SEE_OTHER) {
226  
227              // Check if redirect count is not too large.
228              redirectCount += 1;
229              if (maxRedirects > 0 && redirectCount > maxRedirects) {
230                  connection.disconnect();
231                  throw new PlaylistHasTooManyRedirects(String.format("Too many redirects (%d) for URL %s", redirectCount, url));
232              }
233  
234              // Reconnect to the new URL.
235              currentURL = new URL(connection.getHeaderField("Location"));
236              connection.disconnect();
237              connection = connectToURL(currentURL);
238          }
239  
240          // Return the last connection that did not redirect.
241          return connection;
242      }
243  
244      /**
245       * Start a new connection to a remote URL.
246       *
247       * @param url the remote URL
248       * @return an open connection
249       */
250      protected HttpURLConnection connectToURL(URL url) throws IOException {
251          HttpURLConnection urlConnection = (HttpURLConnection) url.openConnection();
252          urlConnection.setAllowUserInteraction(false);
253          urlConnection.setConnectTimeout(10000);
254          urlConnection.setDoInput(true);
255          urlConnection.setDoOutput(false);
256          urlConnection.setReadTimeout(60000);
257          urlConnection.setUseCaches(true);
258          urlConnection.connect();
259          return urlConnection;
260      }
261  }

