 97                  if (!isValidXForwardedHost(xForwardedHost)) {
 98                      throw new RuntimeException("Cannot calculate proxy uri without HTTP headers: " + X_FORWARDED_HOST + ", " + X_FORWARDED_SERVER + ", " + X_FORWARDED_FOR);
 99                  }
