 66      /**
 67       * Registers the given media file at www.last.fm. This method returns immediately, the actual registration is done
 68       * by a separate thread.
 69       *
 70       * @param mediaFile  The media file to register.
 71       * @param username   last.fm username.
 72       * @param password   last.fm password.
 73       * @param submission Whether this is a submission or a now playing notification.
 74       * @param time       Event time, or {@code null} to use current time.
 75       */
 76      public synchronized void register(MediaFile mediaFile, String username, String password, boolean submission, Instant time) {
 77          if (thread == null) {
 78              thread = new RegistrationThread();
 79              thread.start();
 80          }
 81  
 82          if (queue.size() >= MAX_PENDING_REGISTRATION) {
 83              LOG.warn("Last.fm scrobbler queue is full. Ignoring " + mediaFile);
 84              return;
 85          }
 86  
 87          RegistrationData registrationData = createRegistrationData(mediaFile, username, password, submission, time);
 88          if (registrationData == null) {
 89              return;
 90          }
 91  
 92          try {
 93              queue.put(registrationData);
 94          } catch (InterruptedException x) {
 95              LOG.warn("Interrupted while queuing Last.fm scrobble: " + x.toString());
 96          }
 97      }
 98  
 99      private RegistrationData createRegistrationData(MediaFile mediaFile, String username, String password, boolean submission, Instant time) {
100          RegistrationData reg = new RegistrationData();
101          reg.username = username;
102          reg.password = password;
103          reg.artist = mediaFile.getArtist();
104          reg.album = mediaFile.getAlbumName();
105          reg.title = mediaFile.getTitle();
106          reg.duration = mediaFile.getDuration() == null ? 0 : (int) Math.round(mediaFile.getDuration());
107          reg.time = time == null ? Instant.now() : time;
108          reg.submission = submission;
109  
110          return reg;
111      }
