114       * Scrobbles the given song data at last.fm, using the protocol defined at http://www.last.fm/api/submissions.
115       *
116       * @param registrationData Registration data for the song.
117       */
118      private void scrobble(RegistrationData registrationData) throws URISyntaxException, ClientProtocolException, IOException {
119          if (registrationData == null) {
120              return;
121          }
122  
123          String[] lines = authenticate(registrationData);
124          if (lines == null) {
125              return;
126          }
127  
128          String sessionId = lines[1];
129          String nowPlayingUrl = lines[2];
130          String submissionUrl = lines[3];
131  
132          if (registrationData.submission) {
133              lines = registerSubmission(registrationData, sessionId, submissionUrl);
134          } else {
135              lines = registerNowPlaying(registrationData, sessionId, nowPlayingUrl);
136          }
137  
138          if (lines[0].startsWith("FAILED")) {
139              LOG.warn("Failed to scrobble song '" + registrationData.title + "' at Last.fm: " + lines[0]);
140          } else if (lines[0].startsWith("BADSESSION")) {
141              LOG.warn("Failed to scrobble song '" + registrationData.title + "' at Last.fm.  Invalid session.");
142          } else if (lines[0].startsWith("OK")) {
143              LOG.info("Successfully registered " + (registrationData.submission ? "submission" : "now playing") +
144                        " for song '" + registrationData.title + "' for user " + registrationData.username + " at Last.fm: " + registrationData.time);
145          }
146      }
147  
148      /**
149       * Returns the following lines if authentication succeeds:
150       * <p/>
151       * Line 0: Always "OK"
152       * Line 1: Session ID, e.g., "17E61E13454CDD8B68E8D7DEEEDF6170"
153       * Line 2: URL to use for now playing, e.g., "https://post.audioscrobbler.com:80/np_1.2"
154       * Line 3: URL to use for submissions, e.g., "https://post2.audioscrobbler.com:80/protocol_1.2"
155       * <p/>
156       * If authentication fails, <code>null</code> is returned.
157       */
158      private String[] authenticate(RegistrationData registrationData) throws URISyntaxException, ClientProtocolException, IOException {
159          String clientId = "sub";
160          String clientVersion = "0.1";
161          long timestamp = System.currentTimeMillis() / 1000L;
162          String authToken = calculateAuthenticationToken(registrationData.password, timestamp);
163          URI uri = new URI("http",
164                  /* userInfo= */ null, "post.audioscrobbler.com", -1,
165                  "/",
166                  String.format("hs=true&p=1.2.1&c=%s&v=%s&u=%s&t=%s&a=%s",
167                          clientId, clientVersion, registrationData.username,
168                          timestamp, authToken),
169                  /* fragment= */ null);
170  
171          String[] lines = executeGetRequest(uri);
172  
173          if (lines[0].startsWith("BANNED")) {
174              LOG.warn("Failed to scrobble song '" + registrationData.title + "' at Last.fm. Client version is banned.");
175              return null;
176          }
177  
178          if (lines[0].startsWith("BADAUTH")) {
179              LOG.warn("Failed to scrobble song '" + registrationData.title + "' at Last.fm. Wrong username or password.");
180              return null;
181          }
182  
183          if (lines[0].startsWith("BADTIME")) {
184              LOG.warn("Failed to scrobble song '" + registrationData.title + "' at Last.fm. Bad timestamp, please check local clock.");
185              return null;
186          }
187  
188          if (lines[0].startsWith("FAILED")) {
189              LOG.warn("Failed to scrobble song '" + registrationData.title + "' at Last.fm: " + lines[0]);
190              return null;
191          }
192  
193          if (!lines[0].startsWith("OK")) {
194              LOG.warn("Failed to scrobble song '" + registrationData.title + "' at Last.fm.  Unknown response: " + lines[0]);
195              return null;
196          }
197  
198          return lines;
199      }
