201      private String[] registerSubmission(RegistrationData registrationData, String sessionId, String url) throws UnsupportedEncodingException, ClientProtocolException, IOException {
202          Map<String, String> params = new HashMap<String, String>();
203          params.put("s", sessionId);
204          params.put("a[0]", registrationData.artist);
205          params.put("t[0]", registrationData.title);
206          params.put("i[0]", String.valueOf(registrationData.time.getEpochSecond()));
207          params.put("o[0]", "P");
208          params.put("r[0]", "");
209          params.put("l[0]", String.valueOf(registrationData.duration));
210          params.put("b[0]", registrationData.album);
211          params.put("n[0]", "");
212          params.put("m[0]", "");
213          return executePostRequest(url, params);
214      }
