232      private String[] executeGetRequest(URI url) throws IOException, ClientProtocolException {
233          HttpGet method = new HttpGet(url);
234          method.setConfig(requestConfig);
235          return executeRequest(method);
236      }
237  
238      private String[] executePostRequest(String url, Map<String, String> parameters) throws UnsupportedEncodingException, ClientProtocolException, IOException {
239          List<NameValuePair> params = new ArrayList<NameValuePair>();
240          for (Map.Entry<String, String> entry : parameters.entrySet()) {
241              params.add(new BasicNameValuePair(entry.getKey(), entry.getValue()));
242          }
243  
244          HttpPost request = new HttpPost(url);
245          request.setEntity(new UrlEncodedFormEntity(params, StringUtil.ENCODING_UTF8));
246          request.setConfig(requestConfig);
247          return executeRequest(request);
248      }
249  
250      private String[] executeRequest(HttpUriRequest request) throws ClientProtocolException, IOException {
251          try (CloseableHttpClient client = HttpClients.createDefault()) {
252              ResponseHandler<String> responseHandler = new BasicResponseHandler();
253              String response = client.execute(request, responseHandler);
254              return response.split("\\r?\\n");
255          }
256      }
