232      public static String[] readLines(InputStream in) throws IOException {
233          try (BufferedReader reader = new BufferedReader(new InputStreamReader(in))) {
234              List<String> result = new ArrayList<String>();
235              for (String line = reader.readLine(); line != null; line = reader.readLine()) {
236                  line = line.trim();
237                  if (!line.startsWith("#") && !line.isEmpty()) {
238                      result.add(line);
239                  }
240              }
241              return result.toArray(new String[result.size()]);
242  
243          } finally {
244              FileUtil.closeQuietly(in);
245          }
246      }
