317      /**
318       * Returns the file part of an URL. For instance:
319       * <p/>
320       * <code>
321       * getUrlFile("http://archive.ncsa.uiuc.edu:80/SDG/Software/Mosaic/Demo/url-primer.html")
322       * </code>
323       * <p/>
324       * will return "url-primer.html".
325       *
326       * @param url The URL in question.
327       * @return The file part, or <code>null</code> if no file can be resolved.
328       */
329      public static String getUrlFile(String url) {
330          try {
331              String path = new URL(url).getPath();
332              if (StringUtils.isBlank(path) || path.endsWith("/")) {
333                  return null;
334              }
335  
336              File file = new File(path);
337              String filename = file.getName();
338              if (StringUtils.isBlank(filename)) {
339                  return null;
340              }
341              return filename;
342  
343          } catch (MalformedURLException x) {
344              return null;
345          }
346      }
