23  
24  import java.nio.file.Paths;
25  
26  import static org.junit.Assert.assertFalse;
27  import static org.junit.Assert.assertTrue;
28  
29  /**
30   * Unit test of {@link SecurityService}.
31   *
32   * @author Sindre Mehus
33   */
34  public class SecurityServiceTestCase {
35  
36      @Test
37      public void testIsFileInFolder() {
38          assertTrue(SecurityService.isFileInFolder(Paths.get("/music/foo.mp3"), Paths.get("/")));
39  
40          assertTrue(SecurityService.isFileInFolder(Paths.get("/music/foo.mp3"), Paths.get("/music")));
41          assertTrue(SecurityService.isFileInFolder(Paths.get("/music/foo.mp3"), Paths.get("/music/")));
42          assertTrue(SecurityService.isFileInFolder(Paths.get("\\music\\foo.mp3"), Paths.get("\\music")));
43          assertTrue(SecurityService.isFileInFolder(Paths.get("\\music\\foo.mp3"), Paths.get("\\music\\")));
44          assertTrue(SecurityService.isFileInFolder(Paths.get("music/foo.mp3"), Paths.get("music")));
45          assertTrue(SecurityService.isFileInFolder(Paths.get("music\\foo.mp3"), Paths.get("music")));
46          assertTrue(SecurityService.isFileInFolder(Paths.get("music/foo.mp3"), Paths.get("music/")));
47          assertTrue(SecurityService.isFileInFolder(Paths.get("music\\foo.mp3"), Paths.get("music\\")));
48  
49          assertFalse(SecurityService.isFileInFolder(Paths.get(""), Paths.get("/tmp")));
50          assertFalse(SecurityService.isFileInFolder(Paths.get("foo.mp3"), Paths.get("/tmp")));
51          assertFalse(SecurityService.isFileInFolder(Paths.get("/music/foo.mp3"), Paths.get("/tmp")));
52          assertFalse(SecurityService.isFileInFolder(Paths.get("/music/foo.mp3"), Paths.get("/tmp/music")));
53          assertFalse(SecurityService.isFileInFolder(Paths.get("music/foo.mp3"), Paths.get("/music")));
54  
55          // identity tests
56          assertTrue(SecurityService.isFileInFolder(Paths.get("/music/a"), Paths.get("/music/a")));
57          assertTrue(SecurityService.isFileInFolder(Paths.get("/music/a"), Paths.get("/music/a/")));
58          assertTrue(SecurityService.isFileInFolder(Paths.get("/music/a/"), Paths.get("/music/a/")));
59          assertTrue(SecurityService.isFileInFolder(Paths.get("/music/a/"), Paths.get("/music/a")));
60          assertFalse(SecurityService.isFileInFolder(Paths.get("/music/a2"), Paths.get("/music/a")));
61          assertFalse(SecurityService.isFileInFolder(Paths.get("/music/a"), Paths.get("/music/a2")));
62  
63          // Test with redundant references
64          assertTrue(SecurityService.isFileInFolder(Paths.get("/music/foo..mp3"), Paths.get("/music")));
65          assertTrue(SecurityService.isFileInFolder(Paths.get("/music/foo.."), Paths.get("/music")));
66          assertTrue(SecurityService.isFileInFolder(Paths.get("/music/foo.../"), Paths.get("/music")));
67          assertTrue(SecurityService.isFileInFolder(Paths.get("/music/foo/.."), Paths.get("/music")));
68          assertFalse(SecurityService.isFileInFolder(Paths.get("../music/foo"), Paths.get("/music")));
69          assertTrue(SecurityService.isFileInFolder(Paths.get("/../music/foo"), Paths.get("/music")));
70          assertFalse(SecurityService.isFileInFolder(Paths.get("/music/../foo"), Paths.get("/music")));
71          assertFalse(SecurityService.isFileInFolder(Paths.get("/music/../bar/../foo"), Paths.get("/music")));
72          assertTrue(SecurityService.isFileInFolder(Paths.get("/music\\foo\\.."), Paths.get("/music")));
73          assertFalse(SecurityService.isFileInFolder(Paths.get("..\\music/foo"), Paths.get("/music")));
74          assertFalse(SecurityService.isFileInFolder(Paths.get("/music\\../foo"), Paths.get("/music")));
75          assertFalse(SecurityService.isFileInFolder(Paths.get("/music/..\\bar/../foo"), Paths.get("/music")));
76      }
77  }

