44          assertTrue(SecurityService.isFileInFolder(Paths.get("music/foo.mp3"), Paths.get("music")));
45          assertTrue(SecurityService.isFileInFolder(Paths.get("music\\foo.mp3"), Paths.get("music")));
46          assertTrue(SecurityService.isFileInFolder(Paths.get("music/foo.mp3"), Paths.get("music/")));
47          assertTrue(SecurityService.isFileInFolder(Paths.get("music\\foo.mp3"), Paths.get("music\\")));
