433      @Test
434      public void testUax29() {
435  
436          /*
437           * Case using test resource name
438           */
439  
440          // Semicolon, comma and hyphen.
441          String query = "Bach: Goldberg Variations, BWV 988 - Aria";
442          List<String> terms = toTermString(query);
443          assertEquals(6, terms.size());
444          assertEquals("bach", terms.get(0));
445          assertEquals("goldberg", terms.get(1));
446          assertEquals("variations", terms.get(2));
447          assertEquals("bwv", terms.get(3));
448          assertEquals("988", terms.get(4));
449          assertEquals("aria", terms.get(5));
450  
451          // Underscores around words, ascii and semicolon.
452          query = "_ID3_ARTIST_ Céline Frisch: Café Zimmermann";
453          terms = toTermString(query);
454          assertEquals(5, terms.size());
455  
456          /*
457           * XXX 3.x -> 8.x : _id3_artist_　in UAX#29.
458           * Since the effect is large, trim with Filter.
459           */
460          assertEquals("id3_artist", terms.get(0));
461          assertEquals("celine", terms.get(1));
462          assertEquals("frisch", terms.get(2));
463          assertEquals("cafe", terms.get(3));
464          assertEquals("zimmermann", terms.get(4));
465  
466          // Underscores around words and slashes.
467          query = "_ID3_ARTIST_ Sarah Walker/Nash Ensemble";
468          terms = toTermString(query);
469          assertEquals(5, terms.size());
470  
471          /*
472           * XXX 3.x -> 8.x : _id3_artist_　in UAX#29.
473           * Since the effect is large, trim with Filter.
474           */
475          assertEquals("id3_artist", terms.get(0));
476          assertEquals("sarah", terms.get(1));
477          assertEquals("walker", terms.get(2));
478          assertEquals("nash", terms.get(3));
479          assertEquals("ensemble", terms.get(4));
480  
481          // Space
482          assertEquals(asList("abc", "def"), toTermString(" ABC DEF "));
483          assertEquals(asList("abc1", "def"), toTermString(" ABC1 DEF "));
484  
485          // trim and delimiter
486          assertEquals(asList("abc", "def"), toTermString("+ABC+DEF+"));
487          assertEquals(asList("abc", "def"), toTermString("|ABC|DEF|"));
488          assertEquals(asList("abc", "def"), toTermString("!ABC!DEF!"));
489          assertEquals(asList("abc", "def"), toTermString("(ABC(DEF("));
490          assertEquals(asList("abc", "def"), toTermString(")ABC)DEF)"));
491          assertEquals(asList("abc", "def"), toTermString("{ABC{DEF{"));
492          assertEquals(asList("abc", "def"), toTermString("}ABC}DEF}"));
493          assertEquals(asList("abc", "def"), toTermString("[ABC[DEF["));
494          assertEquals(asList("abc", "def"), toTermString("]ABC]DEF]"));
495          assertEquals(asList("abc", "def"), toTermString("^ABC^DEF^"));
496          assertEquals(asList("abc", "def"), toTermString("\\ABC\\DEF\\"));
497          assertEquals(asList("abc", "def"), toTermString("\"ABC\"DEF\""));
498          assertEquals(asList("abc", "def"), toTermString("~ABC~DEF~"));
499          assertEquals(asList("abc", "def"), toTermString("*ABC*DEF*"));
500          assertEquals(asList("abc", "def"), toTermString("?ABC?DEF?"));
501          assertEquals(asList("abc:def"), toTermString(":ABC:DEF:"));             // XXX 3.x -> 8.x : abc def -> abc:def
502          assertEquals(asList("abc", "def"), toTermString("-ABC-DEF-"));
503          assertEquals(asList("abc", "def"), toTermString("/ABC/DEF/"));
504          /*
505           * XXX 3.x -> 8.x : _abc_def_　in UAX#29.
506           * Since the effect is large, trim with Filter.
507           */
508          assertEquals(asList("abc_def"), toTermString("_ABC_DEF_"));             // XXX 3.x -> 8.x : abc def -> abc_def
509          assertEquals(asList("abc", "def"), toTermString(",ABC,DEF,"));
510          assertEquals(asList("abc.def"), toTermString(".ABC.DEF."));
511          assertEquals(asList("abc", "def"), toTermString("&ABC&DEF&"));          // XXX 3.x -> 8.x : abc&def -> abc def
512          assertEquals(asList("abc", "def"), toTermString("@ABC@DEF@"));          // XXX 3.x -> 8.x : abc@def -> abc def
513          assertEquals(asList("abc'def"), toTermString("'ABC'DEF'"));
514  
515          // trim and delimiter and number
516          assertEquals(asList("abc1", "def"), toTermString("+ABC1+DEF+"));
517          assertEquals(asList("abc1", "def"), toTermString("|ABC1|DEF|"));
518          assertEquals(asList("abc1", "def"), toTermString("!ABC1!DEF!"));
519          assertEquals(asList("abc1", "def"), toTermString("(ABC1(DEF("));
520          assertEquals(asList("abc1", "def"), toTermString(")ABC1)DEF)"));
521          assertEquals(asList("abc1", "def"), toTermString("{ABC1{DEF{"));
522          assertEquals(asList("abc1", "def"), toTermString("}ABC1}DEF}"));
523          assertEquals(asList("abc1", "def"), toTermString("[ABC1[DEF["));
524          assertEquals(asList("abc1", "def"), toTermString("]ABC1]DEF]"));
525          assertEquals(asList("abc1", "def"), toTermString("^ABC1^DEF^"));
526          assertEquals(asList("abc1", "def"), toTermString("\\ABC1\\DEF\\"));
527          assertEquals(asList("abc1", "def"), toTermString("\"ABC1\"DEF\""));
528          assertEquals(asList("abc1", "def"), toTermString("~ABC1~DEF~"));
529          assertEquals(asList("abc1", "def"), toTermString("*ABC1*DEF*"));
530          assertEquals(asList("abc1", "def"), toTermString("?ABC1?DEF?"));
531          assertEquals(asList("abc1", "def"), toTermString(":ABC1:DEF:"));
532          assertEquals(asList("abc1", "def"), toTermString(",ABC1,DEF,"));        // XXX 3.x -> 8.x : abc1,def -> abc1 def
533          assertEquals(asList("abc1", "def"), toTermString("-ABC1-DEF-"));        // XXX 3.x -> 8.x : abc1-def -> abc1 def
534          assertEquals(asList("abc1", "def"), toTermString("/ABC1/DEF/"));        // XXX 3.x -> 8.x : abc1/def -> abc1 def
535          /*
536           * XXX 3.x -> 8.x : _abc1_def_　in UAX#29.
537           * Since the effect is large, trim with Filter.
538           */
539          assertEquals(asList("abc1_def"), toTermString("_ABC1_DEF_"));
540          assertEquals(asList("abc1", "def"), toTermString(".ABC1.DEF."));        // XXX 3.x -> 8.x : abc1.def -> abc1 def
541          assertEquals(asList("abc1", "def"), toTermString("&ABC1&DEF&"));
542          assertEquals(asList("abc1", "def"), toTermString("@ABC1@DEF@"));
543          assertEquals(asList("abc1", "def"), toTermString("'ABC1'DEF'"));
544  
