550      @Test
551      public void testSingleQuotes() {
552  
553          /*
554           * A somewhat cultural that seems to be related to a specific language.
555           */
556          String query = "This is Airsonic's analysis.";
557          List<String> terms = toTermString(query);
558          assertEquals(4, terms.size());
559          assertEquals("this", terms.get(0));// Not deleted because it is not a stopword
560          assertEquals("is", terms.get(1));// Not deleted because it is not a stopword
561          assertEquals("airsonic", terms.get(2));
562          assertEquals("analysis", terms.get(3));
563  
564          /*
565           * XXX 3.x -> 8.x :
566           * we ve -> we've
567           */
568          query = "We’ve been here before.";
569          terms = toTermString(query);
570          assertEquals(4, terms.size());
571          assertEquals("we've", terms.get(0));
572          assertEquals("been", terms.get(1));
573          assertEquals("here", terms.get(2));
574          assertEquals("before", terms.get(3));
575  
576          query = "LʼHomme";
577          terms = toTermString(query);
578          assertEquals(1, terms.size());
579          assertEquals("lʼhomme", terms.get(0));
580  
581          query = "L'Homme";
582          terms = toTermString(query);
583          assertEquals(1, terms.size());
584          assertEquals("l'homme", terms.get(0));
585  
586          query = "aujourd'hui";
587          terms = toTermString(query);
588          assertEquals(1, terms.size());
589          assertEquals("aujourd'hui", terms.get(0));
590  
591          query = "fo'c'sle";
592          terms = toTermString(query);
593          assertEquals(1, terms.size());
594          assertEquals("fo'c'sle", terms.get(0));
595  
596      }
