 51  @Controller
 52  @RequestMapping("/help")
 53  public class HelpController {
 54  
 55      private static final Logger LOG = LoggerFactory.getLogger(HelpController.class);
 56  
 57      private static final int LOG_LINES_TO_SHOW = 50;
 58  
 59      @Autowired
 60      private VersionService versionService;
 61      @Autowired
 62      private SettingsService settingsService;
 63      @Autowired
 64      private SecurityService securityService;
 65  
 66      @GetMapping
 67      protected ModelAndView handleRequestInternal(HttpServletRequest request, HttpServletResponse response) {
 68          Map<String, Object> map = new HashMap<>();
 69  
 70          if (versionService.isNewFinalVersionAvailable()) {
 71              map.put("newVersionAvailable", true);
 72              map.put("latestVersion", versionService.getLatestFinalVersion());
 73          } else if (versionService.isNewBetaVersionAvailable()) {
 74              map.put("newVersionAvailable", true);
 75              map.put("latestVersion", versionService.getLatestBetaVersion());
 76          }
 77  
 78          long totalMemory = Runtime.getRuntime().totalMemory();
 79          long freeMemory = Runtime.getRuntime().freeMemory();
 80  
 81          String serverInfo = request.getSession().getServletContext().getServerInfo() +
 82                              ", java " + System.getProperty("java.version") +
 83                              ", " + System.getProperty("os.name");
 84  
 85          map.put("user", securityService.getCurrentUser(request));
 86          map.put("brand", settingsService.getBrand());
 87          map.put("localVersion", versionService.getLocalVersion());
 88          map.put("buildDate", versionService.getLocalBuildDate());
 89          map.put("buildNumber", versionService.getLocalBuildNumber());
 90          map.put("serverInfo", serverInfo);
 91          map.put("usedMemory", totalMemory - freeMemory);
 92          map.put("totalMemory", totalMemory);
 93          Path logFile = Paths.get(settingsService.getLogFile());
 94          List<String> latestLogEntries = getLatestLogEntries(logFile);
 95          map.put("logEntries", latestLogEntries);
 96          map.put("logFile", logFile);
 97  
 98          return new ModelAndView("help","model",map);
 99      }
100  
101      private static List<String> getLatestLogEntries(Path logFile) {
102          List<String> lines = new LinkedList<>();
103          try (ReversedLinesFileReader reader = new ReversedLinesFileReader(logFile.toFile(), Charset.defaultCharset())) {
104              String current;
105              while ((current = reader.readLine()) != null) {
106                  if (lines.size() >= LOG_LINES_TO_SHOW) {
107                      break;
108                  }
109                  lines.add(0, current);
110              }
111              return lines;
112          } catch (IOException e) {
113              LOG.warn("Could not open log file {}", logFile, e);
114              return null;
115          }
116      }
