 35      public static final String SERVER = System.getProperty("DockerTestingHost", "http://localhost:4040");
 36      public static final String DEFAULT_MUSIC = System.getProperty("DockerTestingDefaultMusicFolder", "/tmp/music");
 37      public static final ObjectMapper MAPPER = new ObjectMapper()
 38              .enable(SerializationFeature.WRAP_ROOT_VALUE)
 39              .enable(DeserializationFeature.UNWRAP_ROOT_VALUE)
 40              .enable(MapperFeature.ACCEPT_CASE_INSENSITIVE_ENUMS);
 41      public static final RestTemplate rest = new RestTemplate();
 42      static {
 43          for (int i = 0; i < rest.getMessageConverters().size(); i++) {
 44              if (rest.getMessageConverters().get(i).getClass() == MappingJackson2HttpMessageConverter.class) {
 45                  rest.getMessageConverters().set(i, new MappingJackson2HttpMessageConverter(MAPPER));
 46              }
 47          }
