 50      public static UriComponentsBuilder addRestParameters(UriComponentsBuilder builder) {
 51          return builder.queryParam("c", "inttest")
 52                  .queryParam("v", "1.15.0")
 53                  .queryParam("u", "admin")
 54                  .queryParam("s", "int")
 55                  .queryParam("t", DigestUtils.md5Hex("admin" + "int"));
 56      }
 57  
 58      public static void doScan() throws Exception {
 59          Assert.assertFalse(isScanning());
 60  
 61          String startScan = rest.getForObject(
 62                  addRestParameters(UriComponentsBuilder.fromHttpUrl(SERVER + "/rest/startScan")).toUriString(),
 63                  String.class);
 64  
 65          System.out.println(startScan);
 66  
 67          Long waitTime = 30000L;
 68          Long sleepTime = 1000L;
 69          while (waitTime > 0 && isScanning()) {
 70              waitTime -= sleepTime;
 71              Thread.sleep(sleepTime);
 72          }
 73  
 74          Assert.assertFalse(isScanning());
 75      }
 76  
 77      private static boolean isScanning() {
 78          return rest.getForObject(
 79                  addRestParameters(UriComponentsBuilder.fromHttpUrl(SERVER + "/rest/getScanStatus"))
 80                          .queryParam("f", "json").toUriString(),
 81                  SubsonicResponse.class)
 82              .getScanStatus().isScanning();
 83      }
