 85      public static void uploadToDefaultMusicFolder(Path directoryPath, String relativePath) {
 86          Path dest = Paths.get(DEFAULT_MUSIC, relativePath);
 87          try {
 88              FileUtils.copyDirectory(directoryPath.toFile(), dest.toFile(), false);
 89          } catch (IOException e) {
 90              throw new RuntimeException(e);
 91          }
 92      }
