 94      public static List<Child> getMediaFilesInMusicFolder() {
 95          List<MusicFolder> musicFolder = rest.getForObject(
 96                  addRestParameters(UriComponentsBuilder.fromHttpUrl(SERVER + "/rest/getMusicFolders"))
 97                          .queryParam("f", "json")
 98                          .toUriString(),
 99                  SubsonicResponse.class)
100              .getMusicFolders().getMusicFolder();
101  
102          MusicFolder music = musicFolder.stream().filter(folder -> Objects.equals(folder.getName(), "Music")).findFirst()
103                  .orElseThrow(() -> new RuntimeException("No top level folder named Music"));
104          return getMediaFiles(music.getId());
105      }
