108          return rest.getForObject(
109                  addRestParameters(UriComponentsBuilder.fromHttpUrl(SERVER + "/rest/getIndexes"))
110                          .queryParam("f", "json")
111                          .queryParam("musicFolderId", folderId)
112                          .toUriString(),
113                  SubsonicResponse.class)
114              .getIndexes().getChild();
115      }
