117      public static byte[] getMediaFileData(String mediaFileId) {
118          HttpHeaders headers = new HttpHeaders();
119          headers.setAccept(MediaType.parseMediaTypes("audio/webm,audio/ogg,audio/wav,audio/*;"));
120          ResponseEntity<byte[]> response = rest.exchange(
121                  addRestParameters(UriComponentsBuilder.fromHttpUrl(SERVER + "/rest/stream"))
122                          .queryParam("id", mediaFileId)
123                          .toUriString(),
124                  HttpMethod.GET,
125                  new HttpEntity<>(headers),
126                  byte[].class);
127  
128          assertThat(response.getBody()).hasSize((int) response.getHeaders().getContentLength());
129          return response.getBody();
130      }
