 67                  captchaOk = recaptchaResponse != null && captcha.isValid(recaptchaResponse);
 68              } else {
 69                  captchaOk = true;
 70              }
 71  
 72              if (!captchaOk) {
 73                  map.put("error", "recover.error.invalidcaptcha");
 74              } else if (user == null) {
 75                  map.put("error", "recover.error.usernotfound");
 76              } else if (user.getEmail() == null) {
 77                  map.put("error", "recover.error.noemail");
 78              } else {
 79                  StringBuilder sb = new StringBuilder(PASSWORD_LENGTH);
 80                  for (int i = 0; i < PASSWORD_LENGTH; i++) {
 81                      int index = random.nextInt(SYMBOLS.length());
 82                      sb.append(SYMBOLS.charAt(index));
 83                  }
 84                  String password = sb.toString();
 85  
 86                  if (emailPassword(password, user.getUsername(), user.getEmail())) {
 87                      map.put("sentTo", user.getEmail());
 88                      user.setLdapAuthenticated(false);
 89                      securityService.updateUser(user);
 90                      UserCredential uc = new UserCredential(user.getUsername(), user.getUsername(), password, securityService.getPreferredPasswordEncoder(true), App.AIRSONIC, "Autogenerated recovery credentials");
 91                      securityService.createCredential(uc);
 92                  } else {
 93                      map.put("error", "recover.error.sendfailed");
 94                  }
 95              }
 96          }
 97  
 98          if (settingsService.isCaptchaEnabled()) {
 99              map.put("recaptchaSiteKey", settingsService.getRecaptchaSiteKey());
100          }
101  
102          return new ModelAndView("recover", "model", map);
103      }
