 54  @Controller
 55  @MessageMapping("/lyrics")
 56  public class LyricsWSController {
 57  
 58      private static final Logger LOG = LoggerFactory.getLogger(LyricsWSController.class);
 59  
 60      /**
 61       * Returns lyrics for the given song and artist.
 62       *
 63       * @return The lyrics, never <code>null</code> .
 64       */
 65      @MessageMapping("/get")
 66      @SendToUser(broadcast = false)
 67      public LyricsInfo getLyrics(LyricsGetRequest req) {
 68          return getLyrics(req.getArtist(), req.getSong());
 69      }
 70  
 71      public LyricsInfo getLyrics(String artist, String song) {
 72          LyricsInfo lyrics = new LyricsInfo();
 73          try {
 74  
 75              artist = StringUtil.urlEncode(artist);
 76              song = StringUtil.urlEncode(song);
 77  
 78              String url = "http://api.chartlyrics.com/apiv1.asmx/SearchLyricDirect?artist=" + artist + "&song=" + song;
 79              String xml = executeGetRequest(url);
 80              lyrics = parseSearchResult(xml);
 81  
 82          } catch (HttpResponseException x) {
 83              LOG.warn("Failed to get lyrics for song '{}'. Request failed: {}", song, x.toString());
 84              if (x.getStatusCode() == 503) {
 85                  lyrics.setTryLater(true);
 86              }
 87          } catch (SocketException | ConnectTimeoutException x) {
 88              LOG.warn("Failed to get lyrics for song '{}': {}", song, x.toString());
 89              lyrics.setTryLater(true);
 90          } catch (Exception x) {
 91              LOG.warn("Failed to get lyrics for song '" + song + "'.", x);
 92          }
 93          return lyrics;
 94      }
 95  
 96      private LyricsInfo parseSearchResult(String xml) throws Exception {
 97          SAXBuilder builder = createSAXBuilder();
 98          Document document = builder.build(new StringReader(xml));
 99  
100          Element root = document.getRootElement();
101          Namespace ns = root.getNamespace();
102  
103          String lyric = StringUtils.trimToNull(root.getChildText("Lyric", ns));
104          String song = root.getChildText("LyricSong", ns);
105          String artist = root.getChildText("LyricArtist", ns);
106  
107          return new LyricsInfo(lyric, artist, song);
108      }
109  
110      private String executeGetRequest(String url) throws IOException {
111          RequestConfig requestConfig = RequestConfig.custom()
112                  .setConnectTimeout(15000)
113                  .setSocketTimeout(15000)
114                  .build();
115          HttpGet method = new HttpGet(url);
116          method.setConfig(requestConfig);
117          try (CloseableHttpClient client = HttpClients.createDefault()) {
118              ResponseHandler<String> responseHandler = new BasicResponseHandler();
119              return client.execute(method, responseHandler);
120          }
121      }
