100      @GetMapping("/export")
101      public ResponseEntity<Resource> exportDB(Principal p, ServletWebRequest swr) throws Exception {
102          Path exportFile = databaseService.exportDB();
103  
104          if (exportFile == null) {
105              return ResponseEntity.internalServerError().build();
106          }
107  
108          User user = securityService.getUserByName(p.getName());
109          Player transferPlayer = playerService.getPlayer(swr.getRequest(), swr.getResponse(), false, false);
110          Supplier<TransferStatus> statusSupplier = () -> statusService.createDownloadStatus(transferPlayer);
111  
112          Consumer<TransferStatus> statusCloser = status -> {
113              statusService.removeDownloadStatus(status);
114              securityService.updateUserByteCounts(user, 0L, status.getBytesTransferred(), 0L);
115              LOG.info("Transferred {} bytes to user: {}, player: {}", status.getBytesTransferred(), user.getUsername(), transferPlayer);
116              databaseService.cleanup(status.getExternalFile());
117          };
118          Resource res = new FileSystemResource(exportFile);
119          Resource monitoredRes = new PipeStreams.MonitoredResource(
120                  res,
121                  settingsService.getDownloadBitrateLimiter(),
122                  statusSupplier,
123                  statusCloser,
124              (input, status) -> {
125                  status.setExternalFile(exportFile);
126                  status.setBytesTotal(FileUtil.size(exportFile));
127              });
128  
129          String filename = StringUtil.fileSystemSafe(exportFile.getFileName().toString());
130          HttpHeaders headers = new HttpHeaders();
131          headers.setContentDisposition(ContentDisposition.builder("attachment").filename(filename, StandardCharsets.UTF_8).build());
132          headers.setContentType(MediaType.parseMediaType(StringUtil.getMimeType(FilenameUtils.getExtension(filename))));
133          return ResponseEntity.ok().headers(headers).body(monitoredRes);
134      }
