116      @Transactional(isolation = Isolation.READ_COMMITTED)
117      public void reassignChildren(MusicFolder from, MusicFolder to) {
118          if (to.getPath().getNameCount() > from.getPath().getNameCount()) {
119              // assign ancestor -> descendant
120              MusicFolder ancestor = from;
121              MusicFolder descendant = to;
122              String relativePath = ancestor.getPath().relativize(descendant.getPath()).toString();
123              // update children
124              int len = relativePath.length();
125              String sql = "update media_file set "
126                      + "folder_id=?, "
127                      + "path=SUBSTR(path, " + (len + 2) + "), "
128                      + "parent_path=(case "
129                      + "  when (length(parent_path) > " + len + ") then SUBSTR(parent_path, " + (len + 2) + ") "
130                      + "  else SUBSTR(parent_path, " + (len + 1) + ") end) "
131                      + "where folder_id=? and path like ?";

