51  
52      @Transactional
53      public SavedPlayQueue getPlayQueue(String username) {
54          SavedPlayQueue playQueue = queryOne("select " + QUERY_COLUMNS + " from play_queue where username=?", rowMapper, username);
55          if (playQueue == null) {
56              return null;
57          }
58          List<Integer> mediaFileIds = queryForInts("select media_file_id from play_queue_file where play_queue_id = ?", playQueue.getId());
59          playQueue.setMediaFileIds(mediaFileIds);
60          return playQueue;
61      }
62  
63      @Transactional
64      public void savePlayQueue(SavedPlayQueue playQueue) {
65          update("delete from play_queue where username=?", playQueue.getUsername());
66          Integer id = insert("play_queue", playQueue);
67          playQueue.setId(id);
68  
69          batchedUpdate("insert into play_queue_file(play_queue_id, media_file_id) values (?, ?)",
70                  playQueue.getMediaFileIds().stream().map(x -> new Object[] { id, x }).collect(Collectors.toList()));
71      }
72  
73      private static class PlayQueueMapper implements RowMapper<SavedPlayQueue> {
74          @Override
75          public SavedPlayQueue mapRow(ResultSet rs, int rowNum) throws SQLException {
76              return new SavedPlayQueue(rs.getInt(1),
77                                        rs.getString(2),
78                                        null,
79                                        rs.getInt(3),
80                                        rs.getLong(4),
81                                        Optional.ofNullable(rs.getTimestamp(5)).map(x -> x.toInstant()).orElse(null),
82                                        rs.getString(6));
83          }
84      }
