 48      private static final String QUERY_COLUMNS = "id, " + INSERT_COLUMNS;
 49      private final PlaylistMapper rowMapper = new PlaylistMapper();
 50      private final static Comparator<Playlist> sorter = Comparator.comparing(p -> p.getName());
 51  
 52      @PostConstruct
 53      public void register() throws Exception {
 54          registerInserts("playlist", "id", Arrays.asList(INSERT_COLUMNS.split(", ")), Playlist.class);
