 56  
 57      public List<Playlist> getReadablePlaylistsForUser(String username) {
 58  
 59          List<Playlist> result1 = getWritablePlaylistsForUser(username);
 60          List<Playlist> result2 = query("select " + QUERY_COLUMNS + " from playlist where is_public", rowMapper);
 61          List<Playlist> result3 = query("select " + prefix(QUERY_COLUMNS, "playlist") + " from playlist, playlist_user where " +
 62                                         "playlist.id = playlist_user.playlist_id and " +
 63                                         "playlist.username != ? and " +
 64                                         "playlist_user.username = ?", rowMapper, username, username);
 65  
 66          // Remove duplicates.
 67          return Stream.of(result1, result2, result3)
 68                  .flatMap(r -> r.parallelStream())
 69                  .filter(LambdaUtils.distinctByKey(p -> p.getId()))
 70                  .sorted(sorter)
 71                  .collect(Collectors.toList());
 72      }
