 74      public List<Playlist> getWritablePlaylistsForUser(String username) {
 75          return query("select " + QUERY_COLUMNS + " from playlist where username=?", rowMapper, username)
 76                  .stream()
 77                  .sorted(sorter)
 78                  .collect(Collectors.toList());
 79      }
