 71      /**
 72       * Returns similar artists, using last.fm REST API.
 73       *
 74       * @param mediaFile         The media file (song, album or artist).
 75       * @param count             Max number of similar artists to return.
 76       * @param includeNotPresent Whether to include artists that are not present in the media library.
 77       * @param musicFolders      Only return artists present in these folders.
 78       * @return Similar artists, ordered by presence then similarity.
 79       */
 80      public List<MediaFile> getSimilarArtists(MediaFile mediaFile, int count, boolean includeNotPresent, List<MusicFolder> musicFolders) {
 81          List<MediaFile> result = new ArrayList<MediaFile>();
 82          if (mediaFile == null) {
 83              return result;
 84          }
 85  
 86          String artistName = getArtistName(mediaFile);
 87          try {
 88              Collection<Artist> similarArtists = Artist.getSimilar(getCanonicalArtistName(artistName), LAST_FM_KEY);
 89  
 90              // First select artists that are present.
 91              for (Artist lastFmArtist : similarArtists) {
 92                  MediaFile similarArtist = mediaFileDao.getArtistByName(lastFmArtist.getName(), musicFolders);
 93                  if (similarArtist != null) {
 94                      result.add(similarArtist);
 95                      if (result.size() == count) {
 96                          return result;
 97                      }
 98                  }
 99              }
100  
101              // Then fill up with non-present artists
102              if (includeNotPresent) {
103                  for (Artist lastFmArtist : similarArtists) {
104                      MediaFile similarArtist = mediaFileDao.getArtistByName(lastFmArtist.getName(), musicFolders);
105                      if (similarArtist == null) {
106                          MediaFile notPresentArtist = new MediaFile();
107                          notPresentArtist.setId(-1);
108                          notPresentArtist.setArtist(lastFmArtist.getName());
109                          result.add(notPresentArtist);
110                          if (result.size() == count) {
111                              return result;
112                          }
113                      }
114                  }
115              }
116  
117          } catch (Throwable x) {
118              LOG.warn("Failed to find similar artists for " + artistName, x);
119          }
120          return result;
121      }
