108      public synchronized void schedule() {
109          long daysBetween = settingsService.getIndexCreationInterval();
110          int hour = settingsService.getIndexCreationHour();
111  
112          if (daysBetween == -1) {
113              LOG.info("Automatic media scanning disabled.");
114              taskService.unscheduleTask("mediascanner-IndexingTask");
115              return;
116          }
117  
118          LocalDateTime now = LocalDateTime.now();
119          LocalDateTime nextRun = now.withHour(hour).withMinute(0).withSecond(0);
120          if (now.compareTo(nextRun) > 0)
121              nextRun = nextRun.plusDays(1);
122  
123          long initialDelayMillis = ChronoUnit.MILLIS.between(now, nextRun);
124          Instant firstTime = Instant.now().plusMillis(initialDelayMillis);
125  
126          taskService.scheduleAtFixedRate("mediascanner-IndexingTask", () -> scanLibrary(), firstTime, Duration.ofDays(daysBetween), true);
127  
128          LOG.info("Automatic media library scanning scheduled to run every {} day(s), starting at {}", daysBetween, nextRun);
129  
130          // In addition, create index immediately if it doesn't exist on disk.
131          if (neverScanned()) {
132              LOG.info("Media library never scanned. Doing it now.");
133              scanLibrary();
134          }
135      }
