137                  .parallelStream()
138                  .map(PodcastChannel::getId)
139                  .map(this::getEpisodes)
140                  .flatMap(List::parallelStream)
141                  .filter(e -> e.getStatus() == PodcastStatus.DOWNLOADING)
142                  .forEach(e -> {
143                      deleteEpisode(e, false);
144                      LOG.info("Deleted Podcast episode '{}' since download was interrupted.", e.getTitle());
145                  });
146              // Reset channel status
147              getAllChannels()
148                  .parallelStream()
149                  .filter(c -> c.getStatus() == PodcastStatus.DOWNLOADING)
150                  .forEach(c -> {
151                      c.setStatus(PodcastStatus.COMPLETED);
152                      updateChannel(c);
153                      LOG.info("Reset channel status '{}' since refresh was interrupted.", c.getTitle());
154                  });
155              schedule();
156          } catch (Throwable x) {
157              LOG.error("Failed to initialize PodcastService", x);
158          }
159      }
