 74      private final List<TransferStatus> streamStatuses = Collections.synchronizedList(new ArrayList<>());
 75      private final List<TransferStatus> downloadStatuses = Collections.synchronizedList(new ArrayList<>());
 76      private final List<TransferStatus> uploadStatuses = Collections.synchronizedList(new ArrayList<>());
