 83      public TransferStatus createStreamStatus(Player player) {
 84          return createStatus(player, streamStatuses);
 85      }
 86  
 87      public void removeStreamStatus(TransferStatus status) {
 88          // Move it to the map of inactive statuses.
 89          inactiveStreamStatuses.compute(status.getPlayer().getId(), (k, v) -> {
 90              streamStatuses.remove(status);
 91              status.setActive(false);
 92              if (v != null) {
 93                  broadcast(getPlayStatus(v), "recent/remove");
 94              }
 95              return status;
 96          });
 97          broadcast(getPlayStatus(status), "recent/add");
 98      }
 99  
100      public List<TransferStatus> getAllStreamStatuses() {
101          List<TransferStatus> snapshot = new ArrayList<>(streamStatuses);
102          Set<Integer> playerIds = snapshot.parallelStream()
103                  .map(x -> x.getPlayer().getId())
104                  .collect(Collectors.toCollection(() -> ConcurrentHashMap.newKeySet()));
105          // Add inactive status for those players that have no active status.
106          return Stream.concat(
107                  snapshot.parallelStream(),
108                  inactiveStreamStatuses.values().parallelStream().filter(s -> !playerIds.contains(s.getPlayer().getId())))
109                  .collect(Collectors.toList());
110      }
111  
112      public List<TransferStatus> getStreamStatusesForPlayer(Player player) {
113          // unsynchronized stream access, but should be okay, we'll just be a bit behind
114          return streamStatuses.parallelStream()
115                  .filter(s -> s.getPlayer().getId().equals(player.getId()))
116                  .collect(Collectors.toList());
117      }
