 75      @Autowired
 76      @Qualifier("dispatchingContentDirectory")
 77      private CustomContentDirectory dispatchingContentDirectory;
 78  
 79      private AtomicReference<Boolean> running = new AtomicReference<>(false);
 80  
 81      @PostConstruct
 82      public void init() {
 83          if (settingsService.isDlnaEnabled() || settingsService.isSonosEnabled()) {
 84              ensureServiceStarted();
 85              if (settingsService.isDlnaEnabled()) {
 86                  // Start DLNA media server?
 87                  setMediaServerEnabled(true);
 88              }
 89          }
 90          Runtime.getRuntime().addShutdownHook(new Thread(() -> ensureServiceStopped()));
 91      }
 92  
 93      public void ensureServiceStarted() {
 94          running.getAndUpdate(bo -> {
 95              if (!bo) {
 96                  startService();
 97                  return true;
 98              } else {
 99                  return true;
100              }
101          });
102      }
103  
104      public void ensureServiceStopped() {
105          running.getAndUpdate(bo -> {
106              if (bo) {
107                  if (upnpService != null) {
108                      LOG.info("Disabling UPnP/DLNA media server");
109                      upnpService.getRegistry().removeAllLocalDevices();
110                      System.err.println("Shutting down UPnP service...");
111                      upnpService.shutdown();
112                      System.err.println("Shutting down UPnP service - Done!");
113                  }
114                  return false;
115              } else {
116                  return false;
117              }
118          });
119  
120      }
121  
122      private void startService() {
123          try {
124              LOG.info("Starting UPnP service...");
125              createService();
126              LOG.info("Successfully started UPnP service on port {}!", settingsService.getUPnpPort());
127          } catch (Throwable x) {
128              LOG.error("Failed to start UPnP service: " + x, x);
129          }
130      }
131  
132      private synchronized void createService() {
133          upnpService = new UpnpServiceImpl(new ApacheUpnpServiceConfiguration(settingsService.getUPnpPort()));
134  
135          // Asynch search for other devices (most importantly UPnP-enabled routers for port-mapping)
136          upnpService.getControlPoint().search();
137  
138      }
139  
140      public void setMediaServerEnabled(boolean enabled) {
141          if (enabled) {
142              ensureServiceStarted();
143              try {
144                  upnpService.getRegistry().addDevice(createMediaServerDevice());
145                  LOG.info("Enabling UPnP/DLNA media server");
146              } catch (Exception x) {
147                  LOG.error("Failed to start UPnP/DLNA media server: " + x, x);
148              }
149          } else {
150              ensureServiceStopped();
151          }
152      }

